import React, { useState } from 'react';

import hashnode from '../api/hashnode.js';


const Blog = () => {

    const [posts, setPosts] = useState();
    
    const query = `
    {
        user(username: "collin-sparkles") {
          publication {
            posts(page: 0) {
              title
              brief
              slug
              dateAdded
              coverImage
            }
          }
        }
      }
    `

    const sendRequest = (e) => {
        e.preventDefault();

        async function getPosts(variables={}) {
            const data = await fetch('https://api.hashnode.com/', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    query,
                    variables
                })
            });
        
            return data.json();
        };
        const result = getPosts();
        console.log(result.data);

        // setPosts(data.user.publication.posts);
        // console.log(posts);
    };

    return (
        <div className="section blog">
            <h1 className="section-title">Check out Sparks' Notes!</h1>
            <div className="blog-content">
                <p>
                    This is blog content!
                </p>
                <button onClick={sendRequest} value="REQUEST">REQUEST</button>
            </div>
        </div>
    );
};

export default Blog;
