import axios from 'axios';
import JSON from 'json';


const KEY = undefined;

const query = `
    {
        user(username: "collin-sparkles") {
          publication {
            posts(page: 0) {
              title
              brief
              slug
              dateAdded
              coverImage
            }
          }
        }
      }
`

export default axios.create({
    baseURL: "https://api.hashnode.com/",
    params: {
        operationName: null,
        query: JSON.stringify(query),
        variables: {},
    }
});
