import axios from 'axios';


const KEY = undefined;

const query = `
    {
        user(username: "collin-sparkles") {
          publication {
            posts(page: 0) {
              title
              brief
              slug
              dateAdded
              coverImage
            }
          }
        }
      }
`

export default axios.create({
    baseURL: "https://api.hashnode.com/",
    params: {
        body: JSON.stringify({
            operationName: null,
            query: query,
            variables: {},
        })
    }
});
